document.addEventListener("DOMContentLoaded",()=>{document.querySelectorAll(".input-list-search").forEach(s=>{const l=s.querySelector("input"),t=s.querySelector(".custom-list"),c=()=>{const e=l.value.trim().toLowerCase();let a=!1;t.querySelectorAll("li").forEach(o=>{const n=o.textContent.toLowerCase().includes(e);o.style.display=n?"block":"none",n&&(a=!0)}),e&&a&&t.classList.remove("hide")};l.addEventListener("focus",()=>{t.classList.remove("hide"),c()}),l.addEventListener("input",c),t.querySelectorAll("li").forEach(e=>{e.addEventListener("click",()=>{l.value=e.textContent,t.classList.add("hide")})}),document.addEventListener("click",e=>{s.contains(e.target)||t.classList.add("hide")})}),document.querySelectorAll(".tag-select").forEach(s=>{new Choices(s,{removeItemButton:!0,placeholderValue:"Виберіть або введіть...",searchPlaceholderValue:"Пошук...",duplicateItemsAllowed:!1})})});
